import React from 'react';
import { View, Text, Image, TouchableOpacity, Linking } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native'
import * as MailComposer from 'expo-mail-composer'

import { Feather } from "@expo/vector-icons"
import logoImg from '../../assets/logo.png'

import style from './style'

export default function Detail() {

    const route = useRoute();
    const navigation = useNavigation();
    const message = `Olá, ${incident.name} estou entrando em contato, para ajudar no ${incident.title}, 
    com o valor de ${incident.Intl.NumberFormat('pt-BR',{
        style:'currency', 
        currency:'BRL'})
        .format(incident.value)} `
    const incident = route.params.incident;

    function navigateToIncidents(){
        navigation.navigate('Incidents')
    }

    function sendMail(){
        MailComposer.composeAsync({
            subject: `Herói do caso: ${incident.title}`,
            recipients: [incident.email],
            body: message
        })
    }

    function sendWhatsapp(){
        Linking.openURL(`whatsapp://send?phone=12345678&text=${message}`)
    }

    return (
        <View style={style.container}>
            <View style={style.header}>
                <Image source={logoImg} />
                <TouchableOpacity onPress={navigateToIncidents}>
                    <Feather name='arrow-left' size={28} color='#e82041' />
                </TouchableOpacity>
            </View>

            <View style={style.incident}>

                <Text style={[style.incidentProperty,{marginTop:0}]}>ONG:</Text>
                <Text style={style.incidentValue}>{incident.name} de {incident.city}/{incident.uf}</Text>

                <Text style={style.incidentProperty}>Caso:</Text>
                <Text style={style.incidentValue}>{incident.title}</Text>

                <Text style={style.incidentProperty}>Valor:</Text>
                <Text style={style.incidentValue}>
                    {Intl.NumberFormat('pt-BR',{
                         style:'currency', 
                         currency:'BRL'})
                         .format(incident.value)}
                </Text>

            </View>

            <View style={style.contactBox}>

                <Text style={style.heroTitle}>Salve o dia!</Text>
                <Text style={style.heroTitle}>Seja o herói deste caso.</Text>
                <Text style={style.heroDescription}>Entre em contato</Text>

                <View style={style.actions}>

                    <TouchableOpacity style={style.action} onPress={sendWhatsapp}>
                        <Text style={style.actionText}>Whatsapp</Text>
                    </TouchableOpacity>
                    
                    <TouchableOpacity style={style.action} onPress={sendMail}>
                        <Text style={style.actionText}>E-mail</Text>
                    </TouchableOpacity>

                </View>
            </View>
        </View>

    )
}
